package day1;

import java.util.Scanner;

public class printAB {
//a와 b 두 숫자를 입력받아 출력
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
        int a = sc.nextInt();
        int b = sc.nextInt();

        System.out.println("a = " + a);
        System.out.println("b = " + b);
	}

}
